import React from 'react'
import Toggle from '../toggle/toggle';
import './navbar.css'

import { themeContext } from '../../mode'
import { useContext } from 'react'

function Navbar() {
    const theme = useContext(themeContext);
    const darkMode = theme.state.darkMode;
  return (
    <div className='navwrapper'>
      <div className="navleft">
          <div className="navname hoveranimation"
          style ={darkMode? {backgroundColor: 'white'} : {backgroundColor : 'black'}}
          >phil akagu-jones</div>
          <Toggle />
      </div>
      <div className="navright">
          <div className="navlist">
              <ul>
                  <li className='hoveranimation'>about</li>
                  <li className='hoveranimation'>work</li>
                  <li className='hoveranimation'>contact</li>
              </ul>
          </div>
      </div>
    </div>
  )
}

export default Navbar;
